local function lni_loader(name)
    slk[name] = lni:packager(name, storm.load)
    print('读取lni文件' .. name)
    for k, v in pairs(slk[name]) do
        if v._parent ~= k then
            if (slk[name][v._parent]) then
                for k1, v1 in pairs(slk[name][v._parent]) do
                    if not v[k1] then
                        slk[name][k][k1] = v1
                    end
                end
            end
        end
    end
end

